import React, { useState, useCallback } from 'react';
import { useDropzone } from 'react-dropzone';
import { Upload, Image as ImageIcon, BarChart3, Download, RefreshCw, Home, Leaf } from 'lucide-react';
import { PredictionResult, ClassPercentage } from './types/index';
import { predictImage, getModelStatus } from './services/api.ts';
import ImageGallery from './components/ImageGallery.tsx';
import ClassDistribution from './components/ClassDistribution.tsx';
import LoadingSpinner from './components/LoadingSpinner.tsx';
import LandingPage from './LandingPage.tsx';
import './App.css';

const App: React.FC = () => {
  const [prediction, setPrediction] = useState<PredictionResult | null>(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [modelStatus, setModelStatus] = useState<{ loaded: boolean; classes: string[] } | null>(null);
  const [showLanding, setShowLanding] = useState(true);

  // Check model status on component mount
  React.useEffect(() => {
    const checkModelStatus = async () => {
      try {
        const status = await getModelStatus();
        setModelStatus(status);
      } catch (err) {
        console.error('Failed to check model status:', err);
      }
    };
    checkModelStatus();
  }, []);

  const onDrop = useCallback(async (acceptedFiles: File[]) => {
    const file = acceptedFiles[0];
    if (!file) return;

    setLoading(true);
    setError(null);
    setPrediction(null);

    try {
      const result = await predictImage(file);
      setPrediction(result);
    } catch (err) {
      setError(err instanceof Error ? err.message : 'Prediction failed');
    } finally {
      setLoading(false);
    }
  }, []);

  const { getRootProps, getInputProps, isDragActive } = useDropzone({
    onDrop,
    accept: {
      'image/*': ['.jpeg', '.jpg', '.png', '.gif', '.bmp', '.webp']
    },
    multiple: false
  });

  const resetPrediction = () => {
    setPrediction(null);
    setError(null);
  };

  if (showLanding) {
    return <LandingPage onGetStarted={() => setShowLanding(false)} />;
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-green-50 to-blue-50">
      {/* Header */}
      <header className="bg-white shadow-sm border-b">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-between items-center py-6">
            <div className="flex items-center space-x-3">
              <div className="w-10 h-10 bg-green-600 rounded-lg flex items-center justify-center">
                <ImageIcon className="w-6 h-6 text-white" />
              </div>
              <div>
                <h1 className="text-2xl font-bold text-gray-900">Deforestation Detection</h1>
                <p className="text-sm text-gray-600">AI-powered land cover segmentation</p>
              </div>
            </div>
            <div className="flex items-center space-x-4">
              {modelStatus && (
                <div className={`px-3 py-1 rounded-full text-sm font-medium ${
                  modelStatus.loaded 
                    ? 'bg-green-100 text-green-800' 
                    : 'bg-red-100 text-red-800'
                }`}>
                  {modelStatus.loaded ? 'Model Ready' : 'Model Loading...'}
                </div>
              )}
            </div>
          </div>
        </div>
      </header>

      <main className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        {!prediction && !loading && (
          <div className="text-center">
            <h2 className="text-3xl font-bold text-gray-900 mb-4">
              Upload Satellite Image
            </h2>
            <p className="text-lg text-gray-600 mb-8">
              Upload a satellite image to detect land cover types and deforestation patterns
            </p>

            {/* Upload Area */}
            <div
              {...getRootProps()}
              className={`max-w-2xl mx-auto border-2 border-dashed rounded-lg p-12 cursor-pointer transition-colors ${
                isDragActive
                  ? 'border-green-400 bg-green-50'
                  : 'border-gray-300 hover:border-green-400 hover:bg-green-50'
              }`}
            >
              <input {...getInputProps()} />
              <div className="text-center">
                <Upload className="w-16 h-16 text-gray-400 mx-auto mb-4" />
                <p className="text-xl font-medium text-gray-900 mb-2">
                  {isDragActive ? 'Drop the image here' : 'Drag & drop an image here'}
                </p>
                <p className="text-gray-600 mb-4">or click to select a file</p>
                <p className="text-sm text-gray-500">
                  Supports: JPG, PNG, GIF, BMP, WebP
                </p>
              </div>
            </div>

            {/* Class Legend */}
            {modelStatus && modelStatus.classes && (
              <div className="mt-12 max-w-4xl mx-auto">
                <h3 className="text-xl font-semibold text-gray-900 mb-6">Land Cover Classes</h3>
                <div className="grid grid-cols-2 md:grid-cols-5 gap-4">
                  {modelStatus.classes.map((className, index) => {
                    const colors = [
                      'bg-cyan-400', // urban
                      'bg-blue-600', // water
                      'bg-green-500', // forest
                      'bg-yellow-400', // agriculture
                      'bg-purple-500' // road
                    ];
                    return (
                      <div key={className} className="flex items-center space-x-2 p-3 bg-white rounded-lg shadow-sm">
                        <div className={`w-4 h-4 rounded ${colors[index]}`}></div>
                        <span className="text-sm font-medium text-gray-700 capitalize">
                          {className}
                        </span>
                      </div>
                    );
                  })}
                </div>
              </div>
            )}
          </div>
        )}

        {/* Loading State */}
        {loading && (
          <div className="text-center py-12">
            <LoadingSpinner />
            <h3 className="text-xl font-semibold text-gray-900 mt-4">
              Analyzing Image...
            </h3>
            <p className="text-gray-600 mt-2">
              This may take a few moments
            </p>
          </div>
        )}

        {/* Error State */}
        {error && (
          <div className="text-center py-12">
            <div className="bg-red-50 border border-red-200 rounded-lg p-6 max-w-md mx-auto">
              <h3 className="text-lg font-semibold text-red-800 mb-2">
                Prediction Failed
              </h3>
              <p className="text-red-600 mb-4">{error}</p>
              <button
                onClick={resetPrediction}
                className="bg-red-600 text-white px-4 py-2 rounded-lg hover:bg-red-700 transition-colors"
              >
                Try Again
              </button>
            </div>
          </div>
        )}

        {/* Results */}
        {prediction && (
          <div className="space-y-8">
            {/* Results Header */}
            <div className="flex justify-between items-center">
              <h2 className="text-3xl font-bold text-gray-900">Analysis Results</h2>
              <button
                onClick={resetPrediction}
                className="flex items-center space-x-2 px-4 py-2 bg-gray-600 text-white rounded-lg hover:bg-gray-700 transition-colors"
              >
                <RefreshCw className="w-4 h-4" />
                <span>Analyze New Image</span>
              </button>
            </div>

            {/* Image Gallery */}
            <ImageGallery
              originalImage={prediction.original_image}
              segmentationMask={prediction.segmentation_mask}
              overlay={prediction.overlay}
            />

            {/* Class Distribution */}
            <ClassDistribution
              classPercentages={prediction.class_percentages}
              originalSize={prediction.original_size}
              predictionSize={prediction.prediction_size}
            />

            {/* Download Section */}
            <div className="bg-white rounded-lg shadow-sm p-6">
              <h3 className="text-lg font-semibold text-gray-900 mb-4 flex items-center">
                <Download className="w-5 h-5 mr-2" />
                Download Results
              </h3>
              <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
                <a
                  href={prediction.original_image}
                  download="original_image.png"
                  className="flex items-center justify-center space-x-2 px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition-colors"
                >
                  <Download className="w-4 h-4" />
                  <span>Original Image</span>
                </a>
                <a
                  href={prediction.segmentation_mask}
                  download="segmentation_mask.png"
                  className="flex items-center justify-center space-x-2 px-4 py-2 bg-green-600 text-white rounded-lg hover:bg-green-700 transition-colors"
                >
                  <Download className="w-4 h-4" />
                  <span>Segmentation Mask</span>
                </a>
                <a
                  href={prediction.overlay}
                  download="overlay.png"
                  className="flex items-center justify-center space-x-2 px-4 py-2 bg-purple-600 text-white rounded-lg hover:bg-purple-700 transition-colors"
                >
                  <Download className="w-4 h-4" />
                  <span>Overlay</span>
                </a>
              </div>
            </div>
          </div>
        )}
      </main>

      {/* Footer */}
      <footer className="bg-white border-t mt-16">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
          <div className="text-center text-gray-600">
            <p>&copy; 2024 Deforestation Detection. Powered by AI and Deep Learning.</p>
          </div>
        </div>
      </footer>
    </div>
  );
};

export default App;
