import React, { useState, useCallback, useRef } from 'react';
import { useDropzone } from 'react-dropzone';
import { 
  Upload, Image as ImageIcon, BarChart3, Download, RefreshCw, Home, 
  Play, Pause, SkipBack, SkipForward, Map, Eye, EyeOff, FileText,
  Calendar, Ruler, AlertTriangle, TrendingUp, Globe, Layers
} from 'lucide-react';
import { PredictionResult } from './types/index';
import { predictImage, getModelStatus } from './services/api.ts';
import ImageGallery from './components/ImageGallery.tsx';
import ClassDistribution from './components/ClassDistribution.tsx';
import LoadingSpinner from './components/LoadingSpinner.tsx';

interface DashboardProps {
  onBackToLanding: () => void;
}

const Dashboard: React.FC<DashboardProps> = ({ onBackToLanding }) => {
  const [beforeImage, setBeforeImage] = useState<File | null>(null);
  const [afterImage, setAfterImage] = useState<File | null>(null);
  const [beforeImagePreview, setBeforeImagePreview] = useState<string | null>(null);
  const [afterImagePreview, setAfterImagePreview] = useState<string | null>(null);
  const [comparisonResult, setComparisonResult] = useState<{
    deforestationDetected: boolean;
    confidence: number;
    beforeAnalysis: PredictionResult | null;
    afterAnalysis: PredictionResult | null;
  } | null>(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [modelStatus, setModelStatus] = useState<{ loaded: boolean; classes: string[] } | null>(null);
  const [activeSection, setActiveSection] = useState<string>('upload');
  const [showHighlighting, setShowHighlighting] = useState(true);
  const [historicalImages, setHistoricalImages] = useState<File[]>([]);
  const [historicalPreviews, setHistoricalPreviews] = useState<string[]>([]);
  const [historicalAnalysis, setHistoricalAnalysis] = useState<PredictionResult[]>([]);
  const [isPlaying, setIsPlaying] = useState(false);
  const [currentFrame, setCurrentFrame] = useState(0);
  const [playbackSpeed, setPlaybackSpeed] = useState(1000); // milliseconds
  const [mapCenter, setMapCenter] = useState({ lat: 0, lng: 0 });
  const [mapZoom, setMapZoom] = useState(10);
  const progressRef = useRef<HTMLDivElement>(null);
  const intervalRef = useRef<NodeJS.Timeout | null>(null);

  // Check model status on component mount
  React.useEffect(() => {
    const checkModelStatus = async () => {
      try {
        const status = await getModelStatus();
        setModelStatus(status);
      } catch (err) {
        console.error('Failed to check model status:', err);
      }
    };
    checkModelStatus();
  }, []);

  const handleBeforeImageUpload = (acceptedFiles: File[]) => {
    const file = acceptedFiles[0];
    if (file) {
      setBeforeImage(file);
      const reader = new FileReader();
      reader.onload = () => setBeforeImagePreview(reader.result as string);
      reader.readAsDataURL(file);
    }
  };

  const handleAfterImageUpload = (acceptedFiles: File[]) => {
    const file = acceptedFiles[0];
    if (file) {
      setAfterImage(file);
      const reader = new FileReader();
      reader.onload = () => setAfterImagePreview(reader.result as string);
      reader.readAsDataURL(file);
    }
  };

  const beforeDropzone = useDropzone({
    onDrop: handleBeforeImageUpload,
    accept: {
      'image/*': ['.jpeg', '.jpg', '.png', '.gif', '.bmp', '.webp']
    },
    multiple: false
  });

  const afterDropzone = useDropzone({
    onDrop: handleAfterImageUpload,
    accept: {
      'image/*': ['.jpeg', '.jpg', '.png', '.gif', '.bmp', '.webp']
    },
    multiple: false
  });

  const analyzeImages = async () => {
    if (!beforeImage || !afterImage) {
      setError('Please upload both before and after images');
      return;
    }

    setLoading(true);
    setError(null);
    setComparisonResult(null);

    // Simulate progress bar
    if (progressRef.current) {
      let progress = 0;
      const interval = setInterval(() => {
        progress += Math.random() * 10;
        if (progress > 90) progress = 90;
        progressRef.current!.style.width = `${progress}%`;
      }, 200);
    }

    try {
      // Analyze both images
      const beforeResult = await predictImage(beforeImage);
      const afterResult = await predictImage(afterImage);

      // Calculate deforestation detection
      const beforeForestPercentage = beforeResult.class_percentages.forest || 0;
      const afterForestPercentage = afterResult.class_percentages.forest || 0;
      const forestLoss = beforeForestPercentage - afterForestPercentage;

      const deforestationDetected = forestLoss > 5; // Threshold of 5% forest loss
      const confidence = Math.min(95, Math.max(60, forestLoss * 10 + 70)); // Confidence based on forest loss

      setComparisonResult({
        deforestationDetected,
        confidence: Math.round(confidence),
        beforeAnalysis: beforeResult,
        afterAnalysis: afterResult
      });

      if (progressRef.current) {
        progressRef.current.style.width = '100%';
      }
    } catch (err) {
      setError(err instanceof Error ? err.message : 'Analysis failed');
    } finally {
      setLoading(false);
      setTimeout(() => {
        if (progressRef.current) {
          progressRef.current.style.width = '0%';
        }
      }, 1000);
    }
  };

  const resetAnalysis = () => {
    setBeforeImage(null);
    setAfterImage(null);
    setBeforeImagePreview(null);
    setAfterImagePreview(null);
    setComparisonResult(null);
    setError(null);
  };

  const resetPrediction = () => {
    setComparisonResult(null);
    setError(null);
  };

  const togglePlayPause = () => {
    setIsPlaying(!isPlaying);
  };

  const generateReport = () => {
    // Simulate PDF generation
    const element = document.createElement('a');
    const file = new Blob(['Deforestation Analysis Report\n\nSummary: Forest cover analysis completed\nArea analyzed: 1000 hectares\nDeforestation detected: 15%'], { type: 'text/plain' });
    element.href = URL.createObjectURL(file);
    element.download = 'deforestation-report.pdf';
    document.body.appendChild(element);
    element.click();
    document.body.removeChild(element);
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-green-50 to-blue-50">
      {/* Header */}
      <header className="bg-white shadow-sm border-b sticky top-0 z-50">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-between items-center py-4">
            <div className="flex items-center space-x-3">
              <div className="w-10 h-10 bg-green-600 rounded-lg flex items-center justify-center">
                <ImageIcon className="w-6 h-6 text-white" />
              </div>
              <div>
                <h1 className="text-xl font-bold text-gray-900">ForestGuard AI Dashboard</h1>
                <p className="text-sm text-gray-600">Comprehensive deforestation analysis</p>
              </div>
            </div>
            <div className="flex items-center space-x-4">
              <button
                onClick={onBackToLanding}
                className="flex items-center space-x-2 px-4 py-2 text-gray-600 hover:text-green-600 transition-colors"
              >
                <Home className="w-4 h-4" />
                <span>Home</span>
              </button>
              {modelStatus && (
                <div className={`px-3 py-1 rounded-full text-sm font-medium ${
                  modelStatus.loaded 
                    ? 'bg-green-100 text-green-800' 
                    : 'bg-red-100 text-red-800'
                }`}>
                  {modelStatus.loaded ? 'Model Ready' : 'Model Loading...'}
                </div>
              )}
            </div>
          </div>
        </div>
      </header>

      {/* Navigation Tabs */}
      <div className="bg-white border-b">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <nav className="flex space-x-8">
            {[
              { id: 'upload', label: 'Image Upload', icon: Upload },
              { id: 'timelapse', label: 'Time-lapse', icon: Play },
              { id: 'map', label: 'Interactive Map', icon: Map },
              { id: 'comparison', label: 'Change Detection', icon: Eye },
              { id: 'reports', label: 'Analytics', icon: FileText }
            ].map(({ id, label, icon: Icon }) => (
              <button
                key={id}
                onClick={() => setActiveSection(id)}
                className={`flex items-center space-x-2 py-4 px-1 border-b-2 font-medium text-sm ${
                  activeSection === id
                    ? 'border-green-500 text-green-600'
                    : 'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300'
                }`}
              >
                <Icon className="w-4 h-4" />
                <span>{label}</span>
              </button>
            ))}
          </nav>
        </div>
      </div>

      <main className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        {/* Section 1: Image Upload & Processing */}
        {activeSection === 'upload' && (
          <div className="space-y-8">
            <div className="text-center">
              <h2 className="text-3xl font-bold text-gray-900 mb-4">
                Deforestation Detection Analysis
              </h2>
              <p className="text-lg text-gray-600 mb-8">
                Upload before and after satellite images to detect deforestation changes
              </p>
            </div>

            {!comparisonResult && !loading && (
              <div className="space-y-8">
                {/* Before Image Upload */}
                <div className="bg-white rounded-lg shadow-sm p-6">
                  <h3 className="text-lg font-semibold text-gray-900 mb-4">Upload Before Image</h3>
                  <div
                    {...beforeDropzone.getRootProps()}
                    className={`border-2 border-dashed rounded-lg p-8 cursor-pointer transition-colors ${
                      beforeDropzone.isDragActive
                        ? 'border-green-400 bg-green-50'
                        : 'border-gray-300 hover:border-green-400 hover:bg-green-50'
                    }`}
                  >
                    <input {...beforeDropzone.getInputProps()} />
                    {beforeImagePreview ? (
                      <div className="text-center">
                        <img
                          src={beforeImagePreview}
                          alt="Before image preview"
                          className="max-w-full max-h-64 mx-auto rounded-lg mb-4"
                        />
                        <p className="text-green-600 font-medium">Before image uploaded successfully</p>
                      </div>
                    ) : (
                      <div className="text-center">
                        <Upload className="w-12 h-12 text-gray-400 mx-auto mb-4" />
                        <p className="text-lg font-medium text-gray-900 mb-2">
                          {beforeDropzone.isDragActive ? 'Drop the image here' : 'Drag & drop before image'}
                        </p>
                        <p className="text-gray-600">or click to select a file</p>
                      </div>
                    )}
                  </div>
                </div>

                {/* After Image Upload */}
                <div className="bg-white rounded-lg shadow-sm p-6">
                  <h3 className="text-lg font-semibold text-gray-900 mb-4">Upload After Image</h3>
                  <div
                    {...afterDropzone.getRootProps()}
                    className={`border-2 border-dashed rounded-lg p-8 cursor-pointer transition-colors ${
                      afterDropzone.isDragActive
                        ? 'border-green-400 bg-green-50'
                        : 'border-gray-300 hover:border-green-400 hover:bg-green-50'
                    }`}
                  >
                    <input {...afterDropzone.getInputProps()} />
                    {afterImagePreview ? (
                      <div className="text-center">
                        <img
                          src={afterImagePreview}
                          alt="After image preview"
                          className="max-w-full max-h-64 mx-auto rounded-lg mb-4"
                        />
                        <p className="text-green-600 font-medium">After image uploaded successfully</p>
                      </div>
                    ) : (
                      <div className="text-center">
                        <Upload className="w-12 h-12 text-gray-400 mx-auto mb-4" />
                        <p className="text-lg font-medium text-gray-900 mb-2">
                          {afterDropzone.isDragActive ? 'Drop the image here' : 'Drag & drop after image'}
                        </p>
                        <p className="text-gray-600">or click to select a file</p>
                      </div>
                    )}
                  </div>
                </div>

                {/* Submit Button */}
                <div className="text-center">
                  <button
                    onClick={analyzeImages}
                    disabled={!beforeImage || !afterImage}
                    className={`px-8 py-4 rounded-lg text-lg font-semibold transition-all ${
                      beforeImage && afterImage
                        ? 'bg-green-600 text-white hover:bg-green-700 transform hover:scale-105'
                        : 'bg-gray-300 text-gray-500 cursor-not-allowed'
                    }`}
                  >
                    Analyze Images for Deforestation
                  </button>
                </div>
              </div>
            )}

            {/* Progress Bar */}
            {loading && (
              <div className="max-w-2xl mx-auto">
                <div className="bg-gray-200 rounded-full h-2 mb-4">
                  <div
                    ref={progressRef}
                    className="bg-green-600 h-2 rounded-full transition-all duration-300 ease-out"
                    style={{ width: '0%' }}
                  ></div>
                </div>
                <div className="text-center">
                  <LoadingSpinner />
                  <h3 className="text-xl font-semibold text-gray-900 mt-4">
                    Analyzing Images...
                  </h3>
                  <p className="text-gray-600 mt-2">
                    AI is comparing before and after images
                  </p>
                </div>
              </div>
            )}

            {/* Results */}
            {comparisonResult && (
              <div className="space-y-8">
                <div className="flex justify-between items-center">
                  <h3 className="text-2xl font-bold text-gray-900">Analysis Results</h3>
                  <button
                    onClick={resetAnalysis}
                    className="flex items-center space-x-2 px-4 py-2 bg-gray-600 text-white rounded-lg hover:bg-gray-700 transition-colors"
                  >
                    <RefreshCw className="w-4 h-4" />
                    <span>Analyze New Images</span>
                  </button>
                </div>

                {/* Detection Result */}
                <div className={`p-6 rounded-lg ${
                  comparisonResult.deforestationDetected 
                    ? 'bg-red-50 border border-red-200' 
                    : 'bg-green-50 border border-green-200'
                }`}>
                  <div className="flex items-center space-x-3 mb-4">
                    {comparisonResult.deforestationDetected ? (
                      <AlertTriangle className="w-8 h-8 text-red-600" />
                    ) : (
                      <Eye className="w-8 h-8 text-green-600" />
                    )}
                    <div>
                      <h4 className={`text-2xl font-bold ${
                        comparisonResult.deforestationDetected ? 'text-red-800' : 'text-green-800'
                      }`}>
                        {comparisonResult.deforestationDetected ? 'Deforestation Detected' : 'No Deforestation Detected'}
                      </h4>
                      <p className={`text-lg ${
                        comparisonResult.deforestationDetected ? 'text-red-600' : 'text-green-600'
                      }`}>
                        Confidence: {comparisonResult.confidence}%
                      </p>
                    </div>
                  </div>
                </div>

                {/* Side-by-side Comparison */}
                <div className="bg-white rounded-lg shadow-sm p-6">
                  <h4 className="text-xl font-semibold text-gray-900 mb-6">Before vs After Comparison</h4>
                  <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
                    <div>
                      <h5 className="text-lg font-medium text-gray-900 mb-4">Before Image</h5>
                      {comparisonResult.beforeAnalysis && (
                        <ImageGallery
                          originalImage={comparisonResult.beforeAnalysis.original_image}
                          segmentationMask={comparisonResult.beforeAnalysis.segmentation_mask}
                          overlay={comparisonResult.beforeAnalysis.overlay}
                        />
                      )}
                    </div>
                    <div>
                      <h5 className="text-lg font-medium text-gray-900 mb-4">After Image</h5>
                      {comparisonResult.afterAnalysis && (
                        <ImageGallery
                          originalImage={comparisonResult.afterAnalysis.original_image}
                          segmentationMask={comparisonResult.afterAnalysis.segmentation_mask}
                          overlay={comparisonResult.afterAnalysis.overlay}
                        />
                      )}
                    </div>
                  </div>
                </div>

                {/* Detailed Analysis */}
                <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
                  <div className="bg-white rounded-lg shadow-sm p-6">
                    <h4 className="text-lg font-semibold text-gray-900 mb-4">Before Analysis</h4>
                    {comparisonResult.beforeAnalysis && (
                      <ClassDistribution
                        classPercentages={comparisonResult.beforeAnalysis.class_percentages}
                        originalSize={comparisonResult.beforeAnalysis.original_size}
                        predictionSize={comparisonResult.beforeAnalysis.prediction_size}
                      />
                    )}
                  </div>
                  <div className="bg-white rounded-lg shadow-sm p-6">
                    <h4 className="text-lg font-semibold text-gray-900 mb-4">After Analysis</h4>
                    {comparisonResult.afterAnalysis && (
                      <ClassDistribution
                        classPercentages={comparisonResult.afterAnalysis.class_percentages}
                        originalSize={comparisonResult.afterAnalysis.original_size}
                        predictionSize={comparisonResult.afterAnalysis.prediction_size}
                      />
                    )}
                  </div>
                </div>
              </div>
            )}

            {/* Error State */}
            {error && (
              <div className="text-center py-12">
                <div className="bg-red-50 border border-red-200 rounded-lg p-6 max-w-md mx-auto">
                  <h3 className="text-lg font-semibold text-red-800 mb-2">
                    Analysis Failed
                  </h3>
                  <p className="text-red-600 mb-4">{error}</p>
                  <button
                    onClick={resetAnalysis}
                    className="bg-red-600 text-white px-4 py-2 rounded-lg hover:bg-red-700 transition-colors"
                  >
                    Try Again
                  </button>
                </div>
              </div>
            )}
          </div>
        )}

        {/* Section 2: Time-lapse Visualization */}
        {activeSection === 'timelapse' && (
          <div className="space-y-8">
            <div className="text-center">
              <h2 className="text-3xl font-bold text-gray-900 mb-4">
                Time-lapse Forest Monitoring
              </h2>
              <p className="text-lg text-gray-600 mb-8">
                Visualize forest cover changes over time with animated sequences
              </p>
            </div>

            <div className="bg-white rounded-lg shadow-sm p-6">
              <div className="flex items-center justify-between mb-6">
                <h3 className="text-xl font-semibold text-gray-900">Historical Analysis</h3>
                <div className="flex items-center space-x-2">
                  <button className="p-2 bg-gray-100 rounded-lg hover:bg-gray-200 transition-colors">
                    <SkipBack className="w-4 h-4" />
                  </button>
                  <button 
                    onClick={togglePlayPause}
                    className="p-2 bg-green-600 text-white rounded-lg hover:bg-green-700 transition-colors"
                  >
                    {isPlaying ? <Pause className="w-4 h-4" /> : <Play className="w-4 h-4" />}
                  </button>
                  <button className="p-2 bg-gray-100 rounded-lg hover:bg-gray-200 transition-colors">
                    <SkipForward className="w-4 h-4" />
                  </button>
                </div>
              </div>

              <div className="aspect-video bg-gray-100 rounded-lg flex items-center justify-center mb-4">
                <div className="text-center">
                  <Calendar className="w-16 h-16 text-gray-400 mx-auto mb-4" />
                  <p className="text-gray-600">Upload historical images to create time-lapse</p>
                </div>
              </div>

              <div className="flex items-center justify-between">
                <div className="flex items-center space-x-4">
                  <span className="text-sm text-gray-600">Time Frame:</span>
                  <input
                    type="range"
                    min="0"
                    max="100"
                    value={currentTimeFrame}
                    onChange={(e) => setCurrentTimeFrame(Number(e.target.value))}
                    className="w-64"
                  />
                  <span className="text-sm text-gray-600">{currentTimeFrame}%</span>
                </div>
                <div className="text-sm text-gray-600">
                  {isPlaying ? 'Playing...' : 'Paused'}
                </div>
              </div>
            </div>
          </div>
        )}

        {/* Section 3: Interactive Map Visualization */}
        {activeSection === 'map' && (
          <div className="space-y-8">
            <div className="text-center">
              <h2 className="text-3xl font-bold text-gray-900 mb-4">
                Interactive Map Analysis
              </h2>
              <p className="text-lg text-gray-600 mb-8">
                Explore deforestation patterns on an interactive map
              </p>
            </div>

            <div className="bg-white rounded-lg shadow-sm p-6">
              <div className="flex items-center justify-between mb-4">
                <h3 className="text-xl font-semibold text-gray-900">Geographic Visualization</h3>
                <div className="flex items-center space-x-2">
                  <button className="px-3 py-1 bg-green-100 text-green-800 rounded-lg text-sm">
                    <Globe className="w-4 h-4 inline mr-1" />
                    Satellite
                  </button>
                  <button className="px-3 py-1 bg-gray-100 text-gray-800 rounded-lg text-sm">
                    <Layers className="w-4 h-4 inline mr-1" />
                    Terrain
                  </button>
                </div>
              </div>

              <div className="aspect-video bg-gray-100 rounded-lg flex items-center justify-center mb-4">
                <div className="text-center">
                  <Map className="w-16 h-16 text-gray-400 mx-auto mb-4" />
                  <p className="text-gray-600">Interactive map will be displayed here</p>
                  <p className="text-sm text-gray-500">Zoom and pan to explore regions</p>
                </div>
              </div>

              <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
                <div className="bg-red-50 p-4 rounded-lg">
                  <div className="flex items-center space-x-2 mb-2">
                    <AlertTriangle className="w-5 h-5 text-red-600" />
                    <span className="font-semibold text-red-800">High Risk Areas</span>
                  </div>
                  <p className="text-sm text-red-600">3 regions detected</p>
                </div>
                <div className="bg-yellow-50 p-4 rounded-lg">
                  <div className="flex items-center space-x-2 mb-2">
                    <TrendingUp className="w-5 h-5 text-yellow-600" />
                    <span className="font-semibold text-yellow-800">Moderate Risk</span>
                  </div>
                  <p className="text-sm text-yellow-600">7 regions detected</p>
                </div>
                <div className="bg-green-50 p-4 rounded-lg">
                  <div className="flex items-center space-x-2 mb-2">
                    <Eye className="w-5 h-5 text-green-600" />
                    <span className="font-semibold text-green-800">Stable Areas</span>
                  </div>
                  <p className="text-sm text-green-600">12 regions monitored</p>
                </div>
              </div>
            </div>
          </div>
        )}

        {/* Section 4: Change Highlighting */}
        {activeSection === 'comparison' && (
          <div className="space-y-8">
            <div className="text-center">
              <h2 className="text-3xl font-bold text-gray-900 mb-4">
                Before & After Comparison
              </h2>
              <p className="text-lg text-gray-600 mb-8">
                Compare satellite images to highlight deforestation changes
              </p>
            </div>

            <div className="bg-white rounded-lg shadow-sm p-6">
              <div className="flex items-center justify-between mb-6">
                <h3 className="text-xl font-semibold text-gray-900">Change Detection</h3>
                <button
                  onClick={() => setShowHighlighting(!showHighlighting)}
                  className={`flex items-center space-x-2 px-4 py-2 rounded-lg transition-colors ${
                    showHighlighting
                      ? 'bg-green-600 text-white hover:bg-green-700'
                      : 'bg-gray-200 text-gray-700 hover:bg-gray-300'
                  }`}
                >
                  {showHighlighting ? <Eye className="w-4 h-4" /> : <EyeOff className="w-4 h-4" />}
                  <span>{showHighlighting ? 'Hide' : 'Show'} Highlighting</span>
                </button>
              </div>

              <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
                <div>
                  <h4 className="text-lg font-semibold text-gray-900 mb-4">Before Image</h4>
                  <div className="aspect-video bg-gray-100 rounded-lg flex items-center justify-center">
                    <div className="text-center">
                      <ImageIcon className="w-12 h-12 text-gray-400 mx-auto mb-2" />
                      <p className="text-gray-600">Upload before image</p>
                    </div>
                  </div>
                </div>
                <div>
                  <h4 className="text-lg font-semibold text-gray-900 mb-4">After Image</h4>
                  <div className="aspect-video bg-gray-100 rounded-lg flex items-center justify-center">
                    <div className="text-center">
                      <ImageIcon className="w-12 h-12 text-gray-400 mx-auto mb-2" />
                      <p className="text-gray-600">Upload after image</p>
                    </div>
                  </div>
                </div>
              </div>

              {showHighlighting && (
                <div className="mt-6 p-4 bg-red-50 rounded-lg">
                  <div className="flex items-center space-x-2 mb-2">
                    <AlertTriangle className="w-5 h-5 text-red-600" />
                    <span className="font-semibold text-red-800">Deforestation Detected</span>
                  </div>
                  <p className="text-sm text-red-600">
                    Highlighted areas show significant forest cover loss
                  </p>
                </div>
              )}
            </div>
          </div>
        )}

        {/* Section 5: Detailed Analytical Reports */}
        {activeSection === 'reports' && (
          <div className="space-y-8">
            <div className="text-center">
              <h2 className="text-3xl font-bold text-gray-900 mb-4">
                Analytical Reports & Insights
              </h2>
              <p className="text-lg text-gray-600 mb-8">
                Generate comprehensive reports with detailed deforestation analysis
              </p>
            </div>

            <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
              <div className="bg-white rounded-lg shadow-sm p-6">
                <h3 className="text-xl font-semibold text-gray-900 mb-4">Report Generation</h3>
                <div className="space-y-4">
                  <button
                    onClick={generateReport}
                    className="w-full flex items-center justify-center space-x-2 px-6 py-3 bg-green-600 text-white rounded-lg hover:bg-green-700 transition-colors"
                  >
                    <FileText className="w-5 h-5" />
                    <span>Generate PDF Report</span>
                  </button>
                  <button className="w-full flex items-center justify-center space-x-2 px-6 py-3 border border-gray-300 text-gray-700 rounded-lg hover:bg-gray-50 transition-colors">
                    <Download className="w-5 h-5" />
                    <span>Export Data (CSV)</span>
                  </button>
                </div>
              </div>

              <div className="bg-white rounded-lg shadow-sm p-6">
                <h3 className="text-xl font-semibold text-gray-900 mb-4">Key Metrics</h3>
                <div className="space-y-4">
                  <div className="flex justify-between items-center">
                    <span className="text-gray-600">Total Area Analyzed</span>
                    <span className="font-semibold">1,250 hectares</span>
                  </div>
                  <div className="flex justify-between items-center">
                    <span className="text-gray-600">Deforestation Rate</span>
                    <span className="font-semibold text-red-600">15.2%</span>
                  </div>
                  <div className="flex justify-between items-center">
                    <span className="text-gray-600">Forest Cover Loss</span>
                    <span className="font-semibold text-red-600">190 hectares</span>
                  </div>
                  <div className="flex justify-between items-center">
                    <span className="text-gray-600">Analysis Date</span>
                    <span className="font-semibold">Dec 2024</span>
                  </div>
                </div>
              </div>
            </div>

            <div className="bg-white rounded-lg shadow-sm p-6">
              <h3 className="text-xl font-semibold text-gray-900 mb-4">Trend Analysis</h3>
              <div className="aspect-video bg-gray-100 rounded-lg flex items-center justify-center">
                <div className="text-center">
                  <BarChart3 className="w-16 h-16 text-gray-400 mx-auto mb-4" />
                  <p className="text-gray-600">Deforestation trend chart will be displayed here</p>
                </div>
              </div>
            </div>
          </div>
        )}
      </main>
    </div>
  );
};

export default Dashboard;
