                      onClick={analyzeImages}
                      disabled={loading}
                      className="px-6 py-3 bg-green-600 text-white rounded-lg hover:bg-green-700 disabled:bg-gray-400 transition-colors"
                    >
                      {loading ? 'Analyzing...' : 'Analyze Changes'}
                    </button>
                  </div>
                )}
              </div>
            ) : (
              /* Comparison Results */
              <div className="space-y-6">
                {/* Controls */}
                <div className="bg-white rounded-lg shadow-sm p-6">
                  <div className="flex items-center justify-between mb-4">
                    <h3 className="text-xl font-semibold text-gray-900">Change Detection Controls</h3>
                    <div className="flex items-center space-x-4">
                      <button
                        onClick={() => setShowHighlighting(!showHighlighting)}
                        className={`flex items-center space-x-2 px-4 py-2 rounded-lg transition-colors ${
                          showHighlighting
                            ? 'bg-green-600 text-white'
                            : 'bg-gray-100 text-gray-700'
                        }`}
                      >
                        {showHighlighting ? <EyeOff className="w-4 h-4" /> : <Eye className="w-4 h-4" />}
                        <span>{showHighlighting ? 'Hide Changes' : 'Show Changes'}</span>
                      </button>
                      <button
                        onClick={resetAnalysis}
                        className="px-4 py-2 bg-red-600 text-white rounded-lg hover:bg-red-700 transition-colors"
                      >
                        Reset Analysis
                      </button>
                    </div>
                  </div>

                  {/* Analysis Results Summary */}
                  {comparisonResult && (
                    <div className="grid grid-cols-1 md:grid-cols-3 gap-4 mb-6">
                      <div className={`p-4 rounded-lg ${
                        comparisonResult.deforestationDetected ? 'bg-red-50' : 'bg-green-50'
                      }`}>
                        <div className="flex items-center space-x-2 mb-2">
                          {comparisonResult.deforestationDetected ? (
                            <AlertTriangle className="w-5 h-5 text-red-600" />
                          ) : (
                            <Eye className="w-5 h-5 text-green-600" />
                          )}
                          <span className={`font-semibold ${
                            comparisonResult.deforestationDetected ? 'text-red-800' : 'text-green-800'
                          }`}>
                            {comparisonResult.deforestationDetected ? 'Deforestation Detected' : 'No Deforestation Detected'}
                          </span>
                        </div>
                        <p className={`text-sm ${
                          comparisonResult.deforestationDetected ? 'text-red-600' : 'text-green-600'
                        }`}>
                          {comparisonResult.deforestationDetected 
                            ? 'Significant forest loss detected between images'
                            : 'No significant forest loss detected'
                          }
                        </p>
                      </div>

                      <div className="bg-blue-50 p-4 rounded-lg">
                        <div className="flex items-center space-x-2 mb-2">
                          <BarChart3 className="w-5 h-5 text-blue-600" />
                          <span className="font-semibold text-blue-800">Confidence</span>
                        </div>
                        <p className="text-2xl font-bold text-blue-600">
                          {comparisonResult.confidence?.toFixed(1)}%
                        </p>
                      </div>

                      <div className="bg-orange-50 p-4 rounded-lg">
                        <div className="flex items-center space-x-2 mb-2">
                          <TrendingUp className="w-5 h-5 text-orange-600" />
                          <span className="font-semibold text-orange-800">Forest Loss</span>
                        </div>
                        <p className="text-2xl font-bold text-orange-600">
                          {comparisonResult.forestLoss?.toFixed(1)}%
                        </p>
                      </div>
                    </div>
                  )}
                </div>

                {/* Side-by-Side Comparison */}
                <div className="bg-white rounded-lg shadow-sm p-6">
                  <h3 className="text-xl font-semibold text-gray-900 mb-4">Side-by-Side Comparison</h3>
                  <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
                    {/* Before Image */}
                    <div>
                      <h4 className="text-lg font-medium text-gray-900 mb-3 flex items-center">
                        <Calendar className="w-4 h-4 mr-2" />
                        Before Image
                      </h4>
                      <div className="relative">
                        <img
                          src={beforeImagePreview}
                          alt="Before image"
                          className="w-full h-64 object-cover rounded-lg border"
                        />
                        {showHighlighting && comparisonResult && (
                          <div className="absolute inset-0 rounded-lg border-2 border-red-500 opacity-50 bg-red-500/20"></div>
                        )}
                      </div>
                      {comparisonResult && (
                        <div className="mt-3 p-3 bg-gray-50 rounded-lg">
                          <h5 className="font-medium text-gray-900 mb-2">Before Analysis</h5>
                          <div className="grid grid-cols-2 gap-2 text-sm">
                            <div className="flex justify-between">
                              <span className="text-gray-600">Forest:</span>
                              <span className="font-medium text-green-600">
                                {comparisonResult.beforeForest?.toFixed(1)}%
                              </span>
                            </div>
                            <div className="flex justify-between">
                              <span className="text-gray-600">Water:</span>
                              <span className="font-medium text-blue-600">
                                {comparisonResult.beforeWater?.toFixed(1)}%
                              </span>
                            </div>
                          </div>
                        </div>
                      )}
                    </div>

                    {/* After Image */}
                    <div>
                      <h4 className="text-lg font-medium text-gray-900 mb-3 flex items-center">
                        <Calendar className="w-4 h-4 mr-2" />
                        After Image
                      </h4>
                      <div className="relative">
                        <img
                          src={afterImagePreview}
                          alt="After image"
                          className="w-full h-64 object-cover rounded-lg border"
                        />
                        {showHighlighting && comparisonResult && (
                          <div className="absolute inset-0 rounded-lg border-2 border-red-500 opacity-50 bg-red-500/20"></div>
                        )}
                      </div>
                      {comparisonResult && (
                        <div className="mt-3 p-3 bg-gray-50 rounded-lg">
                          <h5 className="font-medium text-gray-900 mb-2">After Analysis</h5>
                          <div className="grid grid-cols-2 gap-2 text-sm">
                            <div className="flex justify-between">
                              <span className="text-gray-600">Forest:</span>
                              <span className="font-medium text-green-600">
                                {comparisonResult.afterForest?.toFixed(1)}%
                              </span>
                            </div>
                            <div className="flex justify-between">
                              <span className="text-gray-600">Water:</span>
                              <span className="font-medium text-blue-600">
                                {comparisonResult.afterWater?.toFixed(1)}%
                              </span>
                            </div>
                          </div>
                        </div>
                      )}
                    </div>
                  </div>
                </div>

                {/* Detailed Analysis */}
                {comparisonResult && (
                  <div className="bg-white rounded-lg shadow-sm p-6">
                    <h3 className="text-xl font-semibold text-gray-900 mb-4">Detailed Change Analysis</h3>
                    <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                      {/* Land Cover Changes */}
                      <div>
                        <h4 className="text-lg font-medium text-gray-900 mb-3">Land Cover Changes</h4>
                        <div className="space-y-3">
                          <div className="flex items-center justify-between p-3 bg-green-50 rounded-lg">
                            <span className="text-green-800 font-medium">Forest Coverage</span>
                            <div className="flex items-center space-x-2">
                              <span className="text-sm text-gray-600">
                                {comparisonResult.beforeForest?.toFixed(1)}%  {comparisonResult.afterForest?.toFixed(1)}%
                              </span>
                              <span className={`text-sm font-medium ${
                                (comparisonResult.afterForest || 0) < (comparisonResult.beforeForest || 0)
                                  ? 'text-red-600'
                                  : 'text-green-600'
                              }`}>
                                {((comparisonResult.afterForest || 0) - (comparisonResult.beforeForest || 0)).toFixed(1)}%
                              </span>
                            </div>
                          </div>

                          <div className="flex items-center justify-between p-3 bg-blue-50 rounded-lg">
                            <span className="text-blue-800 font-medium">Water Bodies</span>
                            <div className="flex items-center space-x-2">
                              <span className="text-sm text-gray-600">
                                {comparisonResult.beforeWater?.toFixed(1)}%  {comparisonResult.afterWater?.toFixed(1)}%
                              </span>
                              <span className={`text-sm font-medium ${
                                (comparisonResult.afterWater || 0) > (comparisonResult.beforeWater || 0)
                                  ? 'text-blue-600'
                                  : 'text-gray-600'
                              }`}>
                                {((comparisonResult.afterWater || 0) - (comparisonResult.beforeWater || 0)).toFixed(1)}%
                              </span>
                            </div>
                          </div>

                          <div className="flex items-center justify-between p-3 bg-yellow-50 rounded-lg">
                            <span className="text-yellow-800 font-medium">Agriculture</span>
                            <div className="flex items-center space-x-2">
                              <span className="text-sm text-gray-600">
                                {comparisonResult.beforeAgriculture?.toFixed(1)}%  {comparisonResult.afterAgriculture?.toFixed(1)}%
                              </span>
                              <span className={`text-sm font-medium ${
                                (comparisonResult.afterAgriculture || 0) > (comparisonResult.beforeAgriculture || 0)
                                  ? 'text-yellow-600'
                                  : 'text-gray-600'
                              }`}>
                                {((comparisonResult.afterAgriculture || 0) - (comparisonResult.beforeAgriculture || 0)).toFixed(1)}%
                              </span>
                            </div>
                          </div>
                        </div>
                      </div>

                      {/* Change Statistics */}
                      <div>
                        <h4 className="text-lg font-medium text-gray-900 mb-3">Change Statistics</h4>
                        <div className="space-y-3">
                          <div className="p-3 bg-gray-50 rounded-lg">
                            <div className="flex justify-between items-center mb-2">
                              <span className="text-gray-700">Total Area Analyzed</span>
                              <span className="font-semibold text-gray-900">1,000 hectares</span>
                            </div>
                            <div className="w-full bg-gray-200 rounded-full h-2">
                              <div className="bg-green-600 h-2 rounded-full" style={{width: '100%'}}></div>
                            </div>
                          </div>

                          <div className="p-3 bg-gray-50 rounded-lg">
                            <div className="flex justify-between items-center mb-2">
                              <span className="text-gray-700">Deforested Area</span>
                              <span className="font-semibold text-red-600">
                                {(comparisonResult.forestLoss || 0) * 10} hectares
                              </span>
                            </div>
                            <div className="w-full bg-gray-200 rounded-full h-2">
                              <div 
                                className="bg-red-600 h-2 rounded-full" 
                                style={{width: `${comparisonResult.forestLoss || 0}%`}}
                              ></div>
                            </div>
                          </div>

                          <div className="p-3 bg-gray-50 rounded-lg">
                            <div className="flex justify-between items-center mb-2">
                              <span className="text-gray-700">Analysis Confidence</span>
                              <span className="font-semibold text-blue-600">
                                {comparisonResult.confidence?.toFixed(1)}%
                              </span>
                            </div>
                            <div className="w-full bg-gray-200 rounded-full h-2">
                              <div 
                                className="bg-blue-600 h-2 rounded-full" 
                                style={{width: `${comparisonResult.confidence || 0}%`}}
                              ></div>
                            </div>
                          </div>
                        </div>
                      </div>
                    </div>
                  </div>
                )}

                {/* Loading State */}
                {loading && (
                  <div className="text-center py-12">
                    <LoadingSpinner />
                    <h3 className="text-xl font-semibold text-gray-900 mt-4">
                      Analyzing Changes...
                    </h3>
                    <p className="text-gray-600 mt-2">
                      Comparing before and after images
                    </p>
                  </div>
                )}

                {/* Error State */}
                {error && (
                  <div className="text-center py-12">
                    <div className="bg-red-50 border border-red-200 rounded-lg p-6 max-w-md mx-auto">
                      <h3 className="text-lg font-semibold text-red-800 mb-2">
                        Analysis Failed
                      </h3>
                      <p className="text-red-600 mb-4">{error}</p>
                      <button
                        onClick={() => setError(null)}
                        className="bg-red-600 text-white px-4 py-2 rounded-lg hover:bg-red-700 transition-colors"
                      >
                        Dismiss
                      </button>
                    </div>
                  </div>
                )}
              </div>
            )}
          </div>
        )}

        {/* Section 5: Detailed Analytical Reports */}
        {activeSection === 'reports' && (
          <div className="space-y-8">
            <div className="text-center">
              <h2 className="text-3xl font-bold text-gray-900 mb-4">
                Analytical Reports & Insights
              </h2>
              <p className="text-lg text-gray-600 mb-8">
                Generate comprehensive reports with detailed deforestation analysis
              </p>
            </div>

            <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
              <div className="bg-white rounded-lg shadow-sm p-6">
                <h3 className="text-xl font-semibold text-gray-900 mb-4">Report Generation</h3>
                <div className="space-y-4">
                  <button
                    onClick={generateReport}
                    className="w-full flex items-center justify-center space-x-2 px-6 py-3 bg-green-600 text-white rounded-lg hover:bg-green-700 transition-colors"
                  >
                    <FileText className="w-5 h-5" />
                    <span>Generate PDF Report</span>
                  </button>
                  <button className="w-full flex items-center justify-center space-x-2 px-6 py-3 border border-gray-300 text-gray-700 rounded-lg hover:bg-gray-50 transition-colors">
                    <Download className="w-5 h-5" />
                    <span>Export Data (CSV)</span>
                  </button>
                </div>
              </div>

              <div className="bg-white rounded-lg shadow-sm p-6">
                <h3 className="text-xl font-semibold text-gray-900 mb-4">Key Metrics</h3>
                <div className="space-y-4">
                  <div className="flex justify-between items-center">
                    <span className="text-gray-600">Total Area Analyzed</span>
                    <span className="font-semibold">1,250 hectares</span>
                  </div>
                  <div className="flex justify-between items-center">
                    <span className="text-gray-600">Deforestation Rate</span>
                    <span className="font-semibold text-red-600">15.2%</span>
                  </div>
                  <div className="flex justify-between items-center">
                    <span className="text-gray-600">Forest Cover Loss</span>
                    <span className="font-semibold text-red-600">190 hectares</span>
                  </div>
                  <div className="flex justify-between items-center">
                    <span className="text-gray-600">Analysis Date</span>
                    <span className="font-semibold">Dec 2024</span>
                  </div>
                </div>
              </div>
            </div>

            <div className="bg-white rounded-lg shadow-sm p-6">
              <h3 className="text-xl font-semibold text-gray-900 mb-4">Trend Analysis</h3>
              <div className="aspect-video bg-gray-100 rounded-lg flex items-center justify-center">
                <div className="text-center">
                  <BarChart3 className="w-16 h-16 text-gray-400 mx-auto mb-4" />
                  <p className="text-gray-600">Deforestation trend chart will be displayed here</p>
                </div>
              </div>
            </div>
          </div>
        )}
      </main>
    </div>
  );
};

export default Dashboard;
