import React, { useState, useCallback, useRef } from 'react';
import { useDropzone } from 'react-dropzone';
import { 
  Upload, Image as ImageIcon, BarChart3, Download, RefreshCw, Home, 
  Play, Pause, SkipBack, SkipForward, FileText,
  Calendar, Ruler, AlertTriangle, TrendingUp
} from 'lucide-react';
import { PredictionResult } from './types/index';
import { predictImage, getModelStatus } from './services/api.ts';
import ImageGallery from './components/ImageGallery.tsx';
import ClassDistribution from './components/ClassDistribution.tsx';
import LoadingSpinner from './components/LoadingSpinner.tsx';

interface DashboardProps {
  onBackToLanding: () => void;
}

const Dashboard: React.FC<DashboardProps> = ({ onBackToLanding }) => {
  const [beforeImage, setBeforeImage] = useState<File | null>(null);
  const [afterImage, setAfterImage] = useState<File | null>(null);
  const [beforeImagePreview, setBeforeImagePreview] = useState<string | null>(null);
  const [afterImagePreview, setAfterImagePreview] = useState<string | null>(null);
  const [comparisonResult, setComparisonResult] = useState<{
    deforestationDetected: boolean;
    confidence: number;
    beforeAnalysis: PredictionResult | null;
    afterAnalysis: PredictionResult | null;
  } | null>(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [modelStatus, setModelStatus] = useState<{ loaded: boolean; classes: string[] } | null>(null);
  const [activeSection, setActiveSection] = useState<string>('upload');
  const [historicalImages, setHistoricalImages] = useState<File[]>([]);
  const [historicalPreviews, setHistoricalPreviews] = useState<string[]>([]);
  const [historicalAnalysis, setHistoricalAnalysis] = useState<PredictionResult[]>([]);
  const [isPlaying, setIsPlaying] = useState(false);
  const [currentFrame, setCurrentFrame] = useState(0);
  const [playbackSpeed, setPlaybackSpeed] = useState(1000); // milliseconds

  // Interactive Dashboard states
  const [selectedRegion, setSelectedRegion] = useState<string>('all');
  const [selectedForestType, setSelectedForestType] = useState<string>('all');
  const [selectedTimePeriod, setSelectedTimePeriod] = useState<string>('all');
  const [chartType, setChartType] = useState<'bar' | 'line' | 'pie'>('bar');
  const [dashboardData, setDashboardData] = useState<any[]>([]);
  const [filteredData, setFilteredData] = useState<any[]>([]);
  const progressRef = useRef<HTMLDivElement>(null);
  const intervalRef = useRef<NodeJS.Timeout | null>(null);

  // Check model status on component mount
  React.useEffect(() => {
    const checkModelStatus = async () => {
      try {
        const status = await getModelStatus();
        setModelStatus(status);
      } catch (err) {
        console.error('Failed to check model status:', err);
      }
    };
    checkModelStatus();
  }, []);

  const handleBeforeImageUpload = (acceptedFiles: File[]) => {
    const file = acceptedFiles[0];
    if (file) {
      setBeforeImage(file);
      const reader = new FileReader();
      reader.onload = () => setBeforeImagePreview(reader.result as string);
      reader.readAsDataURL(file);
    }
  };

  const handleAfterImageUpload = (acceptedFiles: File[]) => {
    const file = acceptedFiles[0];
    if (file) {
      setAfterImage(file);
      const reader = new FileReader();
      reader.onload = () => setAfterImagePreview(reader.result as string);
      reader.readAsDataURL(file);
    }
  };

  const beforeDropzone = useDropzone({
    onDrop: handleBeforeImageUpload,
    accept: {
      'image/*': ['.jpeg', '.jpg', '.png', '.gif', '.bmp', '.webp']
    },
    multiple: false
  });

  const afterDropzone = useDropzone({
    onDrop: handleAfterImageUpload,
    accept: {
      'image/*': ['.jpeg', '.jpg', '.png', '.gif', '.bmp', '.webp']
    },
    multiple: false
  });

  const analyzeImages = async () => {
    if (!beforeImage || !afterImage) {
      setError('Please upload both before and after images');
      return;
    }

    setLoading(true);
    setError(null);
    setComparisonResult(null);

    // Simulate progress bar
    if (progressRef.current) {
      let progress = 0;
      const interval = setInterval(() => {
        progress += Math.random() * 10;
        if (progress > 90) progress = 90;
        progressRef.current!.style.width = `${progress}%`;
      }, 200);
    }

    try {
      // Analyze both images
      const beforeResult = await predictImage(beforeImage);
      const afterResult = await predictImage(afterImage);

      // Calculate deforestation detection
      const beforeForestPercentage = beforeResult.class_percentages.forest || 0;
      const afterForestPercentage = afterResult.class_percentages.forest || 0;
      const forestLoss = beforeForestPercentage - afterForestPercentage;

      const deforestationDetected = forestLoss > 5; // Threshold of 5% forest loss
      const confidence = Math.min(95, Math.max(60, forestLoss * 10 + 70)); // Confidence based on forest loss

      setComparisonResult({
        deforestationDetected,
        confidence: Math.round(confidence),
        beforeAnalysis: beforeResult,
        afterAnalysis: afterResult
      });

      if (progressRef.current) {
        progressRef.current.style.width = '100%';
      }
    } catch (err) {
      setError(err instanceof Error ? err.message : 'Analysis failed');
    } finally {
      setLoading(false);
      setTimeout(() => {
        if (progressRef.current) {
          progressRef.current.style.width = '0%';
        }
      }, 1000);
    }
  };

  const resetAnalysis = () => {
    setBeforeImage(null);
    setAfterImage(null);
    setBeforeImagePreview(null);
    setAfterImagePreview(null);
    setComparisonResult(null);
    setError(null);
  };

  const resetPrediction = () => {
    setComparisonResult(null);
    setError(null);
  };

  const handleHistoricalImageUpload = (acceptedFiles: File[]) => {
    const newFiles = [...historicalImages, ...acceptedFiles];
    setHistoricalImages(newFiles);

    // Create previews for new files
    const newPreviews: string[] = [];
    acceptedFiles.forEach(file => {
      const reader = new FileReader();
      reader.onload = () => {
        newPreviews.push(reader.result as string);
        if (newPreviews.length === acceptedFiles.length) {
          setHistoricalPreviews([...historicalPreviews, ...newPreviews]);
        }
      };
      reader.readAsDataURL(file);
    });
  };

  const analyzeHistoricalImages = async () => {
    if (historicalImages.length === 0) return;

    setLoading(true);
    setError(null);

    try {
      const analyses: PredictionResult[] = [];
      for (const image of historicalImages) {
        const result = await predictImage(image);
        analyses.push(result);
      }
      setHistoricalAnalysis(analyses);
    } catch (err) {
      setError(err instanceof Error ? err.message : 'Historical analysis failed');
    } finally {
      setLoading(false);
    }
  };

  const togglePlayPause = () => {
    if (isPlaying) {
      if (intervalRef.current) {
        clearInterval(intervalRef.current);
        intervalRef.current = null;
      }
      setIsPlaying(false);
    } else {
      if (historicalAnalysis.length > 0) {
        intervalRef.current = setInterval(() => {
          setCurrentFrame(prev => (prev + 1) % historicalAnalysis.length);
        }, playbackSpeed);
        setIsPlaying(true);
      }
    }
  };

  const goToFrame = (frameIndex: number) => {
    setCurrentFrame(frameIndex);
    if (intervalRef.current) {
      clearInterval(intervalRef.current);
      intervalRef.current = null;
    }
    setIsPlaying(false);
  };

  const nextFrame = () => {
    setCurrentFrame(prev => (prev + 1) % historicalAnalysis.length);
  };

  const prevFrame = () => {
    setCurrentFrame(prev => (prev - 1 + historicalAnalysis.length) % historicalAnalysis.length);
  };

  const clearHistoricalData = () => {
    setHistoricalImages([]);
    setHistoricalPreviews([]);
    setHistoricalAnalysis([]);
    setCurrentFrame(0);
    if (intervalRef.current) {
      clearInterval(intervalRef.current);
      intervalRef.current = null;
    }
    setIsPlaying(false);
  };

  // Risk Prediction Functions
  const generateRiskData = () => {
    const regions = [
      { id: 1, name: 'Amazon Basin', lat: -3.4653, lng: -62.2159, risk: 0.85, trend: 'increasing' },
      { id: 2, name: 'Congo Basin', lat: 0.2280, lng: 20.8783, risk: 0.72, trend: 'stable' },
      { id: 3, name: 'Borneo', lat: 0.9619, lng: 114.5548, risk: 0.68, trend: 'decreasing' },
      { id: 4, name: 'Southeast Asia', lat: 12.8797, lng: 121.7740, risk: 0.78, trend: 'increasing' },
      { id: 5, name: 'Central America', lat: 15.7835, lng: -90.2308, risk: 0.65, trend: 'stable' },
      { id: 6, name: 'Madagascar', lat: -18.7669, lng: 46.8691, risk: 0.82, trend: 'increasing' },
      { id: 7, name: 'Sumatra', lat: -0.7893, lng: 101.3431, risk: 0.75, trend: 'increasing' },
      { id: 8, name: 'New Guinea', lat: -5.1477, lng: 141.2902, risk: 0.58, trend: 'decreasing' }
    ];
    setRiskData(regions);
  };

  const handleRegionSelect = (region: any) => {
    setSelectedRegion(region);
    setPredictionLoading(true);

    // Simulate prediction loading
    setTimeout(() => {
      setPredictionLoading(false);
    }, 2000);
  };

  const getRiskColor = (risk: number) => {
    if (risk >= 0.8) return 'bg-red-500';
    if (risk >= 0.6) return 'bg-orange-500';
    if (risk >= 0.4) return 'bg-yellow-500';
    return 'bg-green-500';
  };

  const getRiskIntensity = (risk: number) => {
    if (risk >= 0.8) return 'Very High';
    if (risk >= 0.6) return 'High';
    if (risk >= 0.4) return 'Medium';
    return 'Low';
  };

  const getTrendIcon = (trend: string) => {
    switch (trend) {
      case 'increasing': return '↗️';
      case 'decreasing': return '↘️';
      default: return '→';
    }
  };

  // Generate risk data on mount
  React.useEffect(() => {
    generateRiskData();
  }, []);

  // Cleanup interval on unmount
  React.useEffect(() => {
    return () => {
      if (intervalRef.current) {
        clearInterval(intervalRef.current);
      }
    };
  }, []);

  const historicalDropzone = useDropzone({
    onDrop: handleHistoricalImageUpload,
    accept: {
      'image/*': ['.jpeg', '.jpg', '.png', '.gif', '.bmp', '.webp']
    },
    multiple: true
  });

  // Map functionality
  const generateDeforestationData = () => {
    // Generate sample deforestation data based on analysis results
    const data = [];

    if (comparisonResult) {
      // Calculate forest loss from before and after analysis
      const beforeForest = comparisonResult.beforeAnalysis?.class_percentages.forest || 0;
      const afterForest = comparisonResult.afterAnalysis?.class_percentages.forest || 0;
      const forestLoss = beforeForest - afterForest;

      // Add current analysis data
      data.push({
        id: 'current-analysis',
        lat: mapCenter.lat + (Math.random() - 0.5) * 0.1,
        lng: mapCenter.lng + (Math.random() - 0.5) * 0.1,
        area: Math.random() * 1000 + 500, // hectares
        date: new Date().toISOString().split('T')[0],
        severity: comparisonResult.deforestationDetected ? 'high' : 'low',
        forestLoss: forestLoss,
        confidence: comparisonResult.confidence || 0,
        source: 'Current Analysis',
        description: comparisonResult.deforestationDetected 
          ? 'Deforestation detected in this region' 
          : 'No significant deforestation detected'
      });
    }

    // Add historical data if available
    historicalAnalysis.forEach((analysis, index) => {
      data.push({
        id: `historical-${index}`,
        lat: mapCenter.lat + (Math.random() - 0.5) * 0.2,
        lng: mapCenter.lng + (Math.random() - 0.5) * 0.2,
        area: Math.random() * 800 + 200,
        date: new Date(Date.now() - (index + 1) * 30 * 24 * 60 * 60 * 1000).toISOString().split('T')[0],
        severity: analysis.class_percentages.forest < 30 ? 'high' : 'medium',
        forestLoss: Math.max(0, (50 - analysis.class_percentages.forest)),
        confidence: 85 + Math.random() * 15,
        source: `Historical Image ${index + 1}`,
        description: `Historical analysis from ${index + 1} months ago`
      });
    });

    setDeforestationData(data);
  };

  const handleMapClick = (region: any) => {
    setSelectedRegion(region);
  };

  const handleMapViewChange = (view: 'satellite' | 'terrain' | 'roadmap') => {
    setMapView(view);
  };

  const zoomToRegion = (region: any) => {
    setMapCenter({ lat: region.lat, lng: region.lng });
    setMapZoom(12);
    setSelectedRegion(region);
  };

  const clearMapSelection = () => {
    setSelectedRegion(null);
  };

  // Generate deforestation data when analysis results change
  React.useEffect(() => {
    if (comparisonResult || historicalAnalysis.length > 0) {
      generateDeforestationData();
    }
  }, [comparisonResult, historicalAnalysis]);

  const generateReport = () => {
    // Simulate PDF generation
    const element = document.createElement('a');
    const file = new Blob(['Deforestation Analysis Report\n\nSummary: Forest cover analysis completed\nArea analyzed: 1000 hectares\nDeforestation detected: 15%'], { type: 'text/plain' });
    element.href = URL.createObjectURL(file);
    element.download = 'deforestation-report.pdf';
    document.body.appendChild(element);
    element.click();
    document.body.removeChild(element);
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-green-50 to-blue-50">
      {/* Header */}
      <header className="bg-white shadow-sm border-b sticky top-0 z-50">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-between items-center py-4">
            <div className="flex items-center space-x-3">
              <div className="w-10 h-10 bg-green-600 rounded-lg flex items-center justify-center">
                <ImageIcon className="w-6 h-6 text-white" />
              </div>
              <div>
                <h1 className="text-xl font-bold text-gray-900">ForestGuard AI Dashboard</h1>
                <p className="text-sm text-gray-600">Comprehensive deforestation analysis</p>
              </div>
            </div>
            <div className="flex items-center space-x-4">
              <button
                onClick={onBackToLanding}
                className="flex items-center space-x-2 px-4 py-2 text-gray-600 hover:text-green-600 transition-colors"
              >
                <Home className="w-4 h-4" />
                <span>Home</span>
              </button>
              {modelStatus && (
                <div className={`px-3 py-1 rounded-full text-sm font-medium ${
                  modelStatus.loaded 
                    ? 'bg-green-100 text-green-800' 
                    : 'bg-red-100 text-red-800'
                }`}>
                  {modelStatus.loaded ? 'Model Ready' : 'Model Loading...'}
                </div>
              )}
            </div>
          </div>
        </div>
      </header>

      {/* Navigation Tabs */}
      <div className="bg-white border-b">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <nav className="flex space-x-8">
            {[
              { id: 'upload', label: 'Image Upload', icon: Upload },
              { id: 'timelapse', label: 'Time-lapse', icon: Play },
              { id: 'prediction', label: 'Risk Prediction', icon: TrendingUp },
              { id: 'reports', label: 'Analytics', icon: FileText }
            ].map(({ id, label, icon: Icon }) => (
              <button
                key={id}
                onClick={() => setActiveSection(id)}
                className={`flex items-center space-x-2 py-4 px-1 border-b-2 font-medium text-sm ${
                  activeSection === id
                    ? 'border-green-500 text-green-600'
                    : 'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300'
                }`}
              >
                <Icon className="w-4 h-4" />
                <span>{label}</span>
              </button>
            ))}
          </nav>
        </div>
      </div>

      <main className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        {/* Section 1: Image Upload & Processing */}
        {activeSection === 'upload' && (
          <div className="space-y-8">
            <div className="text-center">
              <h2 className="text-3xl font-bold text-gray-900 mb-4">
                Deforestation Detection Analysis
              </h2>
              <p className="text-lg text-gray-600 mb-8">
                Upload before and after satellite images to detect deforestation changes
              </p>
            </div>

            {!comparisonResult && !loading && (
              <div className="space-y-8">
                {/* Before Image Upload */}
                <div className="bg-white rounded-lg shadow-sm p-6">
                  <h3 className="text-lg font-semibold text-gray-900 mb-4">Upload Before Image</h3>
                  <div
                    {...beforeDropzone.getRootProps()}
                    className={`border-2 border-dashed rounded-lg p-8 cursor-pointer transition-colors ${
                      beforeDropzone.isDragActive
                        ? 'border-green-400 bg-green-50'
                        : 'border-gray-300 hover:border-green-400 hover:bg-green-50'
                    }`}
                  >
                    <input {...beforeDropzone.getInputProps()} />
                    {beforeImagePreview ? (
                      <div className="text-center">
                        <img
                          src={beforeImagePreview}
                          alt="Before image preview"
                          className="max-w-full max-h-64 mx-auto rounded-lg mb-4"
                        />
                        <p className="text-green-600 font-medium">Before image uploaded successfully</p>
                      </div>
                    ) : (
                      <div className="text-center">
                        <Upload className="w-12 h-12 text-gray-400 mx-auto mb-4" />
                        <p className="text-lg font-medium text-gray-900 mb-2">
                          {beforeDropzone.isDragActive ? 'Drop the image here' : 'Drag & drop before image'}
                        </p>
                        <p className="text-gray-600">or click to select a file</p>
                      </div>
                    )}
                  </div>
                </div>

                {/* After Image Upload */}
                <div className="bg-white rounded-lg shadow-sm p-6">
                  <h3 className="text-lg font-semibold text-gray-900 mb-4">Upload After Image</h3>
                  <div
                    {...afterDropzone.getRootProps()}
                    className={`border-2 border-dashed rounded-lg p-8 cursor-pointer transition-colors ${
                      afterDropzone.isDragActive
                        ? 'border-green-400 bg-green-50'
                        : 'border-gray-300 hover:border-green-400 hover:bg-green-50'
                    }`}
                  >
                    <input {...afterDropzone.getInputProps()} />
                    {afterImagePreview ? (
                      <div className="text-center">
                        <img
                          src={afterImagePreview}
                          alt="After image preview"
                          className="max-w-full max-h-64 mx-auto rounded-lg mb-4"
                        />
                        <p className="text-green-600 font-medium">After image uploaded successfully</p>
                      </div>
                    ) : (
                      <div className="text-center">
                        <Upload className="w-12 h-12 text-gray-400 mx-auto mb-4" />
                        <p className="text-lg font-medium text-gray-900 mb-2">
                          {afterDropzone.isDragActive ? 'Drop the image here' : 'Drag & drop after image'}
                        </p>
                        <p className="text-gray-600">or click to select a file</p>
                      </div>
                    )}
                  </div>
                </div>

                {/* Submit Button */}
                <div className="text-center">
                  <button
                    onClick={analyzeImages}
                    disabled={!beforeImage || !afterImage}
                    className={`px-8 py-4 rounded-lg text-lg font-semibold transition-all ${
                      beforeImage && afterImage
                        ? 'bg-green-600 text-white hover:bg-green-700 transform hover:scale-105'
                        : 'bg-gray-300 text-gray-500 cursor-not-allowed'
                    }`}
                  >
                    Analyze Images for Deforestation
                  </button>
                </div>
              </div>
            )}

            {/* Progress Bar */}
            {loading && (
              <div className="max-w-2xl mx-auto">
                <div className="bg-gray-200 rounded-full h-2 mb-4">
                  <div
                    ref={progressRef}
                    className="bg-green-600 h-2 rounded-full transition-all duration-300 ease-out"
                    style={{ width: '0%' }}
                  ></div>
                </div>
                <div className="text-center">
                  <LoadingSpinner />
                  <h3 className="text-xl font-semibold text-gray-900 mt-4">
                    Analyzing Images...
                  </h3>
                  <p className="text-gray-600 mt-2">
                    AI is comparing before and after images
                  </p>
                </div>
              </div>
            )}

            {/* Results */}
            {comparisonResult && (
              <div className="space-y-8">
                <div className="flex justify-between items-center">
                  <h3 className="text-2xl font-bold text-gray-900">Analysis Results</h3>
                  <button
                    onClick={resetAnalysis}
                    className="flex items-center space-x-2 px-4 py-2 bg-gray-600 text-white rounded-lg hover:bg-gray-700 transition-colors"
                  >
                    <RefreshCw className="w-4 h-4" />
                    <span>Analyze New Images</span>
                  </button>
                </div>

                {/* Detection Result */}
                <div className={`p-6 rounded-lg ${
                  comparisonResult.deforestationDetected 
                    ? 'bg-red-50 border border-red-200' 
                    : 'bg-green-50 border border-green-200'
                }`}>
                  <div className="flex items-center space-x-3 mb-4">
                    {comparisonResult.deforestationDetected ? (
                      <AlertTriangle className="w-8 h-8 text-red-600" />
                    ) : (
                      <BarChart3 className="w-8 h-8 text-green-600" />
                    )}
                    <div>
