                  </thead>
                  <tbody className="bg-white divide-y divide-gray-200">
                    {filteredData.map((item: any) => (
                      <tr key={item.id} className="hover:bg-gray-50">
                        <td className="px-6 py-4 whitespace-nowrap text-sm font-medium text-gray-900">{item.region}</td>
                        <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">{item.forestType}</td>
                        <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">{item.timePeriod}</td>
                        <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                          <span className={`px-2 py-1 rounded-full text-xs font-medium ${
                            item.forestCover >= 70 ? 'bg-green-100 text-green-800' :
                            item.forestCover >= 50 ? 'bg-yellow-100 text-yellow-800' :
                            'bg-red-100 text-red-800'
                          }`}>
                            {item.forestCover}%
                          </span>
                        </td>
                        <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                          <span className={`px-2 py-1 rounded-full text-xs font-medium ${
                            item.deforestation >= 4 ? 'bg-red-100 text-red-800' :
                            item.deforestation >= 2 ? 'bg-yellow-100 text-yellow-800' :
                            'bg-green-100 text-green-800'
                          }`}>
                            {item.deforestation}%
                          </span>
                        </td>
                        <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">{item.area.toLocaleString()}</td>
                        <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">{item.carbon.toLocaleString()}</td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            </div>

            {/* Summary Statistics */}
            <div className="grid grid-cols-1 md:grid-cols-4 gap-6">
              <div className="bg-white rounded-lg shadow-sm p-6">
                <div className="flex items-center">
                  <div className="flex-shrink-0">
                    <div className="w-8 h-8 bg-green-100 rounded-full flex items-center justify-center">
                      <BarChart3 className="w-4 h-4 text-green-600" />
                    </div>
                  </div>
                  <div className="ml-4">
                    <p className="text-sm font-medium text-gray-500">Total Regions</p>
                    <p className="text-2xl font-semibold text-gray-900">{filteredData.length}</p>
                  </div>
                </div>
              </div>

              <div className="bg-white rounded-lg shadow-sm p-6">
                <div className="flex items-center">
                  <div className="flex-shrink-0">
                    <div className="w-8 h-8 bg-blue-100 rounded-full flex items-center justify-center">
                      <TrendingUp className="w-4 h-4 text-blue-600" />
                    </div>
                  </div>
                  <div className="ml-4">
                    <p className="text-sm font-medium text-gray-500">Avg Forest Cover</p>
                    <p className="text-2xl font-semibold text-gray-900">
                      {(filteredData.reduce((sum: number, item: any) => sum + item.forestCover, 0) / filteredData.length || 0).toFixed(1)}%
                    </p>
                  </div>
                </div>
              </div>

              <div className="bg-white rounded-lg shadow-sm p-6">
                <div className="flex items-center">
                  <div className="flex-shrink-0">
                    <div className="w-8 h-8 bg-red-100 rounded-full flex items-center justify-center">
                      <AlertTriangle className="w-4 h-4 text-red-600" />
                    </div>
                  </div>
                  <div className="ml-4">
                    <p className="text-sm font-medium text-gray-500">Avg Deforestation</p>
                    <p className="text-2xl font-semibold text-gray-900">
                      {(filteredData.reduce((sum: number, item: any) => sum + item.deforestation, 0) / filteredData.length || 0).toFixed(1)}%
                    </p>
                  </div>
                </div>
              </div>

              <div className="bg-white rounded-lg shadow-sm p-6">
                <div className="flex items-center">
                  <div className="flex-shrink-0">
                    <div className="w-8 h-8 bg-purple-100 rounded-full flex items-center justify-center">
                      <Ruler className="w-4 h-4 text-purple-600" />
                    </div>
                  </div>
                  <div className="ml-4">
                    <p className="text-sm font-medium text-gray-500">Total Area</p>
                    <p className="text-2xl font-semibold text-gray-900">
                      {(filteredData.reduce((sum: number, item: any) => sum + item.area, 0) / 1000000).toFixed(1)}M km
                    </p>
                  </div>
                </div>
              </div>
            </div>
          </div>
        )}

        {/* Section 4: Detailed Analytical Reports */}
        {activeSection === 'reports' && (
          <div className="space-y-8">
            <div className="text-center">
              <h2 className="text-3xl font-bold text-gray-900 mb-4">
                Analytical Reports & Insights
              </h2>
              <p className="text-lg text-gray-600 mb-8">
                Generate comprehensive reports with detailed deforestation analysis
              </p>
            </div>

            <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
              <div className="bg-white rounded-lg shadow-sm p-6">
                <h3 className="text-xl font-semibold text-gray-900 mb-4">Report Generation</h3>
                <div className="space-y-4">
                  <button
                    onClick={generateReport}
                    className="w-full flex items-center justify-center space-x-2 px-6 py-3 bg-green-600 text-white rounded-lg hover:bg-green-700 transition-colors"
                  >
                    <FileText className="w-5 h-5" />
                    <span>Generate PDF Report</span>
                  </button>
                  <button className="w-full flex items-center justify-center space-x-2 px-6 py-3 border border-gray-300 text-gray-700 rounded-lg hover:bg-gray-50 transition-colors">
                    <Download className="w-5 h-5" />
                    <span>Export Data (CSV)</span>
                  </button>
                </div>
              </div>

              <div className="bg-white rounded-lg shadow-sm p-6">
                <h3 className="text-xl font-semibold text-gray-900 mb-4">Key Metrics</h3>
                <div className="space-y-4">
                  <div className="flex justify-between items-center">
                    <span className="text-gray-600">Total Area Analyzed</span>
                    <span className="font-semibold">1,250 hectares</span>
                  </div>
                  <div className="flex justify-between items-center">
                    <span className="text-gray-600">Deforestation Rate</span>
                    <span className="font-semibold text-red-600">15.2%</span>
                  </div>
                  <div className="flex justify-between items-center">
                    <span className="text-gray-600">Forest Cover Loss</span>
                    <span className="font-semibold text-red-600">190 hectares</span>
                  </div>
                  <div className="flex justify-between items-center">
                    <span className="text-gray-600">Analysis Date</span>
                    <span className="font-semibold">Dec 2024</span>
                  </div>
                </div>
              </div>
            </div>

            <div className="bg-white rounded-lg shadow-sm p-6">
              <h3 className="text-xl font-semibold text-gray-900 mb-4">Trend Analysis</h3>
              <div className="aspect-video bg-gray-100 rounded-lg flex items-center justify-center">
                <div className="text-center">
                  <BarChart3 className="w-16 h-16 text-gray-400 mx-auto mb-4" />
                  <p className="text-gray-600">Deforestation trend chart will be displayed here</p>
                </div>
              </div>
            </div>
          </div>
        )}
      </main>
    </div>
  );
};

export default Dashboard;
