                  </div>
                  <div className="ml-4">
                    <p className="text-sm font-medium text-gray-500">Avg Forest Cover</p>
                    <p className="text-2xl font-semibold text-gray-900">
                      {metrics.avgForest.toFixed(1)}%
                    </p>
                  </div>
                </div>
              </div>

              <div className="bg-white rounded-lg shadow-sm p-6">
                <div className="flex items-center">
                  <div className="flex-shrink-0">
                    <div className="w-8 h-8 bg-red-100 rounded-full flex items-center justify-center">
                      <AlertTriangle className="w-4 h-4 text-red-600" />
                    </div>
                  </div>
                  <div className="ml-4">
                    <p className="text-sm font-medium text-gray-500">Avg Deforestation</p>
                    <p className="text-2xl font-semibold text-gray-900">
                      {metrics.avgDefor.toFixed(1)}%
                    </p>
                  </div>
                </div>
              </div>

              <div className="bg-white rounded-lg shadow_sm p-6">
                <div className="flex items-center">
                  <div className="flex-shrink-0">
                    <div className="w-8 h-8 bg-purple-100 rounded-full flex items-center justify-center">
                      <Ruler className="w-4 h-4 text-purple-600" />
                    </div>
                  </div>
                  <div className="ml-4">
                    <p className="text-sm font-medium text-gray-500">Total Area</p>
                    <p className="text-2xl font-semibold text-gray-900">
                      {(metrics.totalArea / 1_000_000).toFixed(1)}M km
                    </p>
                  </div>
                </div>
              </div>
            </div>
          </div>
        )}
      </main>
    </div>
  );
};

export default Dashboard;
